var link=document.querySelector(".button-contacts"),popup=document.querySelector(".modal-write-us"),
close=popup.querySelector(".modal-close"),form=popup.querySelector("form"),
user=popup.querySelector("[name=user]"),email=popup.querySelector("[name=email]"),
isStorageSupport=!0,storage="";try{storage=localStorage.getItem("user")}catch(e){isStorageSupport=!1}link.addEventListener("click",function(e){e.preventDefault(),
popup.classList.add("modal-show"),storage?(user.value=storage,email.focus()):user.focus()}),close.addEventListener("click",function(e){e.preventDefault(),
popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){user.value&&email.value?isStorageSupport&&localStorage.setItem("email",email.value):(e.preventDefault(),console.log("Нужно ввести логин и пароль"),
popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),
window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),
popup.classList.remove("modal-error")))});